INSERT INTO `boi` VALUES ('1', 'Anarchist Cookbook', 'Spawns six troll bombs around the room. Recharge time: 3', 'Collectible', 'Vanilla', '1407600534', '0');
INSERT INTO `boi` VALUES ('2', 'The Bean', 'Causes Isaac to have bad gas, poisoning any enemy in close vicinity. Recharge time: 1', 'Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('3', 'Bob\'s Rotten Head', 'Throws a bomb in the direction Isaac\'s facing. It does extreme damage, poisons the enemy for 5 seconds, and destroys rocks. Recharge time: 3', 'Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('4', 'The Bible', 'Upon activation, Isaac gains Flight, allowing him to pass over all obstacles in the current room. Instantly kills Mom, Mom\'s Heart and It Lives when activated, will kill Isaac when activated while fighting Satan, Isaac or ??? unless he has The Wafer. Recharge time: 6', 'Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('5', 'The Book Of Belial', 'Adds 2 points of damage for the current room. Damage is further multiplied by 1.5x with the Blood Of The Martyr item. Also increases chances of a Devil Room spawning by 25%. Recharge time: 3', 'Collectible', 'Vanilla', '1407602778', '0');
INSERT INTO `boi` VALUES ('6', 'Book Of Revelations', 'Adds 1 Soul Heart and makes the next boss a Horseman (except on XL Floors and on floors with fixed bosses such as Mom). Increases chances of a Devil Room spawning by 35%. Recharge time: 6', 'Collectible', 'Vanilla', '1407600518', '0');
INSERT INTO `boi` VALUES ('7', 'Book Of Shadows', 'Grants Isaac invulnerability for 10 seconds. Recharge time: 3', 'Collectible', 'Vanilla', '1407600524', '0');
INSERT INTO `boi` VALUES ('8', 'The Book Of Sin', 'Spawns a random Pick Up, Pill or Card. Recharge time: 4', 'Collectible', 'Vanilla', '1407600529', '0');
INSERT INTO `boi` VALUES ('9', 'Dead Sea Scrolls', 'Uses a random Activated Item. Recharge time: 4', 'Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('10', 'Deck Of Cards', 'Gives Isaac a random Tarot Card. Recharge time: 6', 'Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('11', 'The Dice', 'Allows Isaac to re-roll collectible items in the current room into other items, depending on the current room\'s Item Pool. Recharge time: 3', 'Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('12', 'Doctor\'s Remote', 'Sets a big red target on the ground (similar to the Epic Fetus item) which can be controlled with the arrow keys or the mouse. After 6 seconds or by pressing space again, a huge explosive missile hits it, which damages all enemies caught in its blast radius. Recharge time: 3', 'Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('13', 'Forget Me Now', 'When activated, the level ends and is freshly regenerated with new rooms, monsters, and items. Disappears after use. One-time use.', 'Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('14', 'The Gamekid', 'Isaac turns into a Pac-Man-like creature for 5 seconds, gaining invincibility, +0.28 Speed, and the ability to damage enemies by touch, restoring a half-heart of health for every 2 enemies killed. Enemies will also turn blue and start fleeing from Isaac for the duration of the effect. Recharge time: 6', 'Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('15', 'The Hourglass', 'Upon activation, enemies and their projectiles are slowed down for 7.67 seconds. Recharge time: 2', 'Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('16', 'Kamikaze!', 'Creates an explosion in the same area as Isaac, dealing one heart of damage to him and also damaging all enemies caught in the blast radius. Recharge time: 0', 'Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('17', 'Lemon Mishap', 'Creates a small yellow pool of Creep, which damages enemies that cross it. Recharge time: 2', 'Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('18', 'Mom\'s Bottle Of Pills', 'Gives Isaac a random Pill, and will cause him to drop any held Q-slot item (If any). The player will only receive six different pill types in one playthrough. Recharge time: 6', 'Collectible', 'Vanilla', '1407602702', '0');
INSERT INTO `boi` VALUES ('19', 'Mom\'s Bra', 'Causes Isaac to wear a bra on his head for a short period of time, which freezes enemies for 5 seconds. Recharge time: 3', 'Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('20', 'Mom\'s Pad', 'Causes enemies to back away from Isaac for 5 seconds. Recharge time: 3', 'Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('21', 'Monster Manuel', 'Summons a random familiar for the current room only, even if it is locked. Recharge time: 4', 'Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('22', 'Monstro\'s Tooth', 'Monstro jumps onto a random enemy, doing damage. Cannot be used in a fight against Monstro, but can be used against Monstro II. Recharge time: 3', 'Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('23', 'Mr. Boom', 'Drops a large bomb. Recharge time: 2', 'Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('24', 'My Little Unicorn', 'Grants Isaac invincibility for a short period of time. Isaac cannot fire tears during the invincibility effect, but he can damage enemies by touch. Increases Speed by 0.28 for the duration of the effect. Recharge time: 6', 'Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('25', 'The Nail', 'Adds one Soul Heart and allows the breaking of Rocks and damaging enemies by simply walking over them. Increases Damage by 0.7 and decreases Speed by 0.18. Recharge time: 6', 'Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('26', 'The Necronomicon', 'Damages all enemies in the room significantly. Recharge time: 6', 'Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('27', 'A Pony', 'Upon use, Isaac will charge in the direction selected, and will become invincible during the charge, allowing him to damage enemies by touch. While equipped, passively increases Speed to a minimum of 1.5, and grants Isaac Flight. Recharge time: 4', 'Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('28', 'The Poop', 'Drops a pile of poop at his current location. This pile of poop may yield Hearts or Coins like regular poop. Recharge time: 1', 'Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('29', 'Razor Blade', 'Deals 1 Heart of damage to Isaac, which increases damage for the current room. Recharge time: 0', 'Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('30', 'The Pinking Shears', 'Separates Isaac\'s head and body for the current room. Isaac\'s head will be controlled, which now has Flight, while his body will seek out enemies to attack by contact damage. Recharge time: 6', 'Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('31', 'Shoop Da Whoop!', 'Fires a laser from Isaac\'s mouth. Recharge time: 3', 'Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('32', 'Tammy\'s Head', 'Shoots 10 tears in a circle around Isaac, which retain the size (damage), shot speed, and special effects (such as Poison) of Isaac\'s tears. The tears are not affected by tear-replacing items like Brimstone. Recharge time: 1', 'Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('33', 'Teleport!', 'Teleports Isaac to a random room on the current floor. Recharge time: 2', 'Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('34', 'We Need To Go Deeper!', 'Creates a trapdoor to go to the next floor. If used in The Womb, it will create a trapdoor to Sheol, even if the player hasn\'t unlocked it. Does not work in Sheol, The Cathedral, or The Chest. Recharge time: 6', 'Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('35', 'Yum Heart', 'Restores 1 whole heart. Recharge time: 4', 'Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('36', 'Best Friend', 'Deploys a decoy Isaac that eventually explodes with an increased blast radius. Like all explosive attacks, the explosion can hurt Isaac if he is within the blast radius. Recharge time: 3', 'Collectible', 'Wrath of the Lamb', '0', '0');
INSERT INTO `boi` VALUES ('37', 'Blood Rights', 'Deals a half-heart of damage to Isaac, which damages all enemies in the room. Recharge time: 0', 'Collectible', 'Wrath of the Lamb', '0', '0');
INSERT INTO `boi` VALUES ('38', 'The Candle', 'Throws out a blue flame that can pass over gaps (but not through rocks) that damages anything in its path. Recharges 3 seconds after firing.', 'Collectible', 'Wrath of the Lamb', '0', '0');
INSERT INTO `boi` VALUES ('39', 'Crack The Sky', 'Summons multiple beams of light in random locations around the room, dealing a large amount of damage to any enemy that touches them. Recharge time: 4', 'Collectible', 'Wrath of the Lamb', '0', '0');
INSERT INTO `boi` VALUES ('40', 'Crystal Ball', 'Reveals the map and drops a random Tarot Card or Soul Heart. Recharge time: 6', 'Collectible', 'Wrath of the Lamb', '0', '0');
INSERT INTO `boi` VALUES ('41', 'D20', 'Randomly changes all Pick Ups in the current room. Recharge time: 6', 'Collectible', 'Wrath of the Lamb', '0', '0');
INSERT INTO `boi` VALUES ('42', 'Dad\'s Key', 'Opens all doors in the current room when activated, except rooms which require a Key to open, such as Treasure Rooms and Libraries. Recharge time: 2', 'Collectible', 'Wrath of the Lamb', '0', '0');
INSERT INTO `boi` VALUES ('43', 'Guppy\'s Head', 'Spawns 2-4 Blue Flies to damage enemies. Recharge time: 1', 'Collectible', 'Wrath of the Lamb', '0', '0');
INSERT INTO `boi` VALUES ('44', 'Guppy\'s Paw', 'Removes one Heart Container in exchange for gaining 3 Soul Hearts. Recharge time: 0', 'Collectible', 'Wrath of the Lamb', '0', '0');
INSERT INTO `boi` VALUES ('45', 'IV Bag', 'Upon activation, Isaac loses a half-heart (one whole heart in The Womb and onwards) for a random amount of coins, similar to the Blood Donation Machine. Recharge time: 0', 'Collectible', 'Wrath of the Lamb', '0', '0');
INSERT INTO `boi` VALUES ('46', 'Notched Axe', 'Breaks a single rock when activated. Recharge time: 1', 'Collectible', 'Wrath of the Lamb', '0', '0');
INSERT INTO `boi` VALUES ('47', 'Portable Slot', 'Upon activation, 1 coin will be consumed, and a random Pick Up may drop, similar to the Slot Machine. Recharge time: 0', 'Collectible', 'Wrath of the Lamb', '0', '0');
INSERT INTO `boi` VALUES ('48', 'Prayer Card', 'Grants half an Eternal Heart per use. Recharge time: 6', 'Collectible', 'Wrath of the Lamb', '0', '0');
INSERT INTO `boi` VALUES ('49', 'Remote Detonator', 'Bombs you place explode only when you use this item. Gives 5 bombs. Recharge time: 0', 'Collectible', 'Wrath of the Lamb', '0', '0');
INSERT INTO `boi` VALUES ('50', 'Spider Butt', 'Damages all enemies in the room and and slows them and their projectiles down for 2.5 seconds (similar to The Hourglass). Recharge time: 2', 'Collectible', 'Wrath of the Lamb', '0', '0');
INSERT INTO `boi` VALUES ('51', 'Telepathy For Dummies', 'Gives Isaac homing tears for the current room. (Identical to Spoon Bender) Recharge time: 2', 'Collectible', 'Wrath of the Lamb', '0', '0');
INSERT INTO `boi` VALUES ('52', 'White Pony', 'Upon use, Isaac will become invincible and charge in the direction selected, dealing damage to monsters touched. In addition, beams of light (similar to Crack The Sky) drop down in random locations around the room, damaging any enemies that touch them. While equipped, increases Speed up to a minimum of 1.5, and grants Isaac Flight. Recharge time: 6', 'Collectible', 'Wrath of the Lamb', '0', '0');
INSERT INTO `boi` VALUES ('53', '<3', 'Increases available heart containers by 1 and restores all health.', 'Passive Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('54', '1UP', 'Gives an extra life. Upon death, you will respawn in the previous room with full health and lose the 1UP.', 'Passive Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('55', '9 Volt', 'Leaves Isaac with two bars already charged after using an item. Upon pickup, Isaac\'s current activated item will be fully charged.', 'Passive Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('56', 'The Battery', 'Gives Isaac an extra charge every 15 seconds per room, up to a maximum of 3 charges.', 'Passive Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('57', 'The Belt', 'Increases Speed by 2.', 'Passive Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('58', 'Blood Bag', 'Increases HP by 1, Speed by 0.3, and restores 4 hearts. More than one can be obtained during a same playthrough.', 'Passive Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('59', 'Blood Of The Martyr', 'Increases Damage by 1, and turns Isaac\'s tears into blood tears. If collected with the Book Of Belial, damage will also be multiplied by 1.5x upon activation.', 'Passive Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('60', 'Bobby-Bomb', 'Gives Isaac 5 Bombs, and causes all dropped bombs to home towards the closest enemy.', 'Passive Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('61', 'Bomb Bag', 'Isaac is followed by a small bag that drops a Bomb, 1+1 Free, or a Troll Bomb every 3 rooms.', 'Passive Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('62', 'Boom!', 'Gives 10 bombs.', 'Passive Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('63', 'Breakfast', 'Increases HP by 1.', 'Passive Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('64', 'Brimstone', 'Changes tears to a chargeable laser, which pierces all objects in its path. Also multiplies damage by 3. It can only be fired after a short charge-up period.', 'Passive Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('65', 'Brother Bobby', 'Follows Isaac around shooting normal tears.', 'Passive Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('66', 'Bucket Of Lard', 'Gives two empty heart containers, heals by half a heart but decreases speed by 1.', 'Passive Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('67', 'Charm Of The Vampire', 'Killing 13 monsters heals the player for half a heart.', 'Passive Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('68', 'Chocolate Milk', 'Allows Isaac to charge shots, making them a lot stronger and larger, and turns them brown. Tears will no longer automatically fire while holding down arrow keys.', 'Passive Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('69', 'The Common Cold', 'Gives Isaac\'s shot a chance of poisoning enemies for a 2.5 seconds, and turns tears green.', 'Passive Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('70', 'The Compass', 'Reveals all special rooms (Boss Rooms, Treasure Rooms, Shops, etc.), but not Secret Rooms/Super Secret Rooms or regular rooms.', 'Passive Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('71', 'Cube Of Meat', 'The first pick up gives a floating cube similar to a Pretty Fly that damages enemies and blocks shots. The second pick up changes it into a floating head that shoots red tears and blocks shots. The third pick up changes it into a humanoid Meat Boy, who walks around chasing enemies, but no longer shoot projectiles. (This stage and the last stage can push bombs around, which can damage Isaac if the player is not careful). The fourth pick up increases his size and damage. A fifth pick up will create another cube of meat orbital which then advances to another Super Meat Boy. However, it is not possible to have three or more Meat Boys, as the ninth cube will simply disappear on pickup.', 'Passive Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('72', 'Cupid\'s Arrow', 'Gives Isaac piercing tears that can travel through enemies. Removes knockback effect of tears.', 'Passive Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('73', 'Dead Bird', 'Whenever Isaac takes damage, the bird starts attacking nearby enemies while flying over obstacles. The effect only lasts for the current room.', 'Passive Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('74', 'Dead Cat', 'Your HP is set to 1. You are followed by a floating cat head and have 9 lives (these still count as deaths on the statistics screen). You can increase your max HP as normal, but after each death it will reset to 1 again.', 'Passive Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('75', 'Dessert', 'Increases available heart containers by 1.', 'Passive Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('76', 'Dinner', 'Increases available heart containers by 1.', 'Passive Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('77', 'Distant Admiration', 'A red attack fly circles further out than the Halo of Flies/Pretty Flies, but closer than Forever Alone. Deals more damage than Forever Alone.', 'Passive Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('78', 'Demon Baby', 'Follows Isaac and automatically fires at enemies in its reach.', 'Passive Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('79', 'A Dollar', 'Gives Isaac 99 coins. Can be found multiple times in a single playthrough.', 'Passive Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('80', 'Dr. Fetus', 'Replaces Isaac\'s shot with a bomb. Only one bomb can be shot at a time, and it will slide a fixed range before exploding.', 'Passive Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('81', 'Forever Alone', 'Spawns a blue attack fly that orbits a long distance from Isaac.', 'Passive Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('82', 'Growth Hormones', 'Increase Speed and Damage by 2 each.', 'Passive Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('83', 'Guardian Angel', 'Spawns a Guardian Angel that orbits Isaac. Does damage on touch, blocks shots and doubles the speed of other orbitals.', 'Passive Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('84', 'The Halo', 'Increases all stats (Including Heart Containers) by one.', 'Passive Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('85', 'Halo Of Flies', 'Grants 2 Fly orbitals that circle Isaac. Blocks shots and damages Fly enemies on touch.', 'Passive Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('86', 'The Inner Eye', 'Gives Isaac a triple shot, sets Tears to minimum', 'Passive Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('87', 'The Ladder', 'Allows Isaac to walk across gaps one square across by automatically placing a ladder between the two walkables.', 'Passive Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('88', 'Little C.H.A.D.', 'Isaac is followed by Little C.H.A.D, who will drop a half Heart every 2 rooms.', 'Passive Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('89', 'Little Chubby', 'Adds a small grub that follows Isaac. Charges forward and deals touch damage to any enemies in her path. Will attack when the Isaac attacks with a moderate cooldown time before she can attack again.', 'Passive Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('90', 'Little Gish', 'Follows Isaac around shooting tar projectiles that reduce enemies\' movement speed, shot speed, and range for a few seconds.', 'Passive Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('91', 'Little Steve', 'Follows Isaac around, shooting homing projectiles.', 'Passive Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('92', 'Loki\'s Horns', 'Gives a random chance for projectiles to fire in a cross (+) pattern.', 'Passive Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('93', 'Lord Of The Pit', 'Isaac can fly over all obstacles, including damage floors, spikes, rocks, and pits. Increases speed by 1.', 'Passive Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('94', 'Lucky Foot', 'All Pills picked up will have positive or neutral effects (same effect as the PHD, but without pill identification and neutral pills). Increases the chance of getting Pick Ups from clearing a room or from Poop and Fireplaces. Increases the chance of winning the Shell game in the Arcade room from 33% to roughly 50%. Increases the chances of getting items from Golden Chests.', 'Passive Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('95', 'Lunch', 'Increases available heart containers by 1.', 'Passive Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('96', 'A Lump Of Coal', 'Increases damage of tears based on amount of time in the air. 0.14 damage per frame, or 4.2 damage per second.(tear size increases as it gets further away).', 'Passive Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('97', 'Magic Mushroom', 'Fully heals Isaac, increases all Stats (excluding Rate Of Fire) by 1, and multiplies damage by 1.5x.', 'Passive Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('98', 'Magneto', 'Pulls Pick Ups towards Isaac.', 'Passive Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('99', 'The Mark', 'Increases Damage by 1, Speed by 0.2, and adds one Soul Heart.', 'Passive Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('100', 'Max\'s Head', 'Increases damage by 1, and multiplies it by 1.5x.', 'Passive Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('101', 'Mini Mushroom', 'Increases Speed and Range by 1.', 'Passive Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('102', 'Mom\'s Contacts', 'Gives Isaac a petrifying shot that will randomly freeze enemies solid for a few seconds.', 'Passive Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('103', 'Mom\'s Eye', 'Allows Isaac to fire tears from the back of his head.', 'Passive Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('104', 'Mom\'s Heels', 'Increases Range by 7.75.', 'Passive Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('105', 'Mom\'s Knife', 'Once collected, Isaac no longer shoots tears, he shoots a knife in boomerang style. The range of throw increases by charging the shot. Can also be used as a melee weapon while not fired.', 'Passive Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('106', 'Mom\'s Lipstick', 'Increases Range by 7.75.', 'Passive Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('107', 'Mom\'s Underwear', 'Increases Range by 7.75.', 'Passive Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('108', 'Money = Power', 'Increases damage based on the number of coins the player has at the moment.', 'Passive Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('109', 'Mr. Mega', 'Gives Isaac 5 bombs, and makes all dropped bombs larger (Similar to the bomb dropped by Mr. Boom), which have an increased blast radius and increased damage.', 'Passive Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('110', 'My Reflection', 'Gives Isaac\'s tears a boomerang effect, and sets range to default.', 'Passive Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('111', 'Number One', 'Sets Isaac\'s tears to their maximum rate of fire and minimum range.', 'Passive Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('112', 'Odd Mushroom (Large)', 'Increases damage by 0.3, adds an empty Heart Container, increases range by 2.75 and reduces Speed by 0.1.', 'Passive Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('113', 'Odd Mushroom (Thin)', 'Increases Speed by 1, sets Rate Of Fire to maximum while reducing Damage by 1.', 'Passive Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('114', 'Ouija Board', 'Turns Isaac\'s tears spectral, allowing them to go through rocks or obstacles.', 'Passive Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('115', 'The Pact', 'Increases Damage by 0.5 and Rate Of Fire by 0.7. The player also gains 2 Soul Hearts.', 'Passive Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('116', 'The Parasite', 'Makes the Isaac\'s tears split in two when they hit something.', 'Passive Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('117', 'Pentagram', 'Increases Damage by 1 and increases chances of the Devil Room appearing by 20%.', 'Passive Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('118', 'PHD', 'Changes negative Pill effects into positive ones. Also causes Blood Donation Machines to drop more money. Restores 2 hearts upon pickup.', 'Passive Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('119', 'A Quarter', 'Gives you 25 pennies.', 'Passive Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('120', 'Raw Liver', 'Increases available Heart Containers by 2 and completely restores health.', 'Passive Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('121', 'The Relic', 'Isaac is followed by a small blue cross that will drop a Soul Heart every 5 rooms.', 'Passive Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('122', 'Robo-Baby', 'Follows Isaac around, shooting enemies with an eye laser similar to Technology.', 'Passive Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('123', 'Roid Rage', 'Increases Speed by 0.6. Does NOT increase range.', 'Passive Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('124', 'Rosary', 'Adds 3 Soul Hearts and increases the chance for The Bible to appear in the Shop.', 'Passive Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('125', 'Rotten Meat', 'Increases available heart containers by 1.', 'Passive Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('126', 'Sack Of Pennies', 'Isaac is followed by a small bag that drops a coin every 2 rooms. Usually a penny, sometimes a nickel.', 'Passive Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('127', 'The Sad Onion', 'Increases tears by 0.7.', 'Passive Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('128', 'Sister Maggy', 'Follows Isaac around shooting blood tears.', 'Passive Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('129', 'Skatole', 'All the various fly type enemies no longer harm you, shoot, or split.', 'Passive Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('130', 'Skeleton Key', 'Sets your key count to the maximum of 99.', 'Passive Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('131', 'The Small Rock', 'Increases damage by 1, increases tears by 0.2, decreases speed by 0.2.', 'Passive Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('132', 'Spelunker Hat', 'Reveals the Secret Room and Top Secret Room when passing by it and reveals if a room contains a mini-boss.', 'Passive Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('133', 'Spider\'s Bite', 'Gives Isaac\'s shots a chance of slowing enemies for 2.5 seconds.', 'Passive Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('134', 'Spoon Bender', 'Gives Isaac\'s shots the ability to curve towards enemies.', 'Passive Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('135', 'Steam Sale', 'Reduces all Shop prices by 50%, rounded down when an item, rounded up when a pickup.', 'Passive Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('136', 'Steven', 'Increases Damage by 1.', 'Passive Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('137', 'Super Bandage', 'Increases HP by 1 and gives 2 soul hearts.', 'Passive Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('138', 'Technology', 'Gives Isaac a laser shot with unlimited range. Shots pierce enemies but not rocks, fire, or poop. Shots can\'t be angled, and will not provide knockback.', 'Passive Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('139', 'Transcendence', 'Removes Isaac\'s body, allowing flight over obstacles, spikes and pits.', 'Passive Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('140', 'Treasure Map', 'Reveals all rooms for every floor, including the Secret Room. However, it does not reveal icons.', 'Passive Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('141', 'The Virus', 'Isaac will automatically poison any enemy that he touches.', 'Passive Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('142', 'The Wafer', 'Turns all types of damage into a half-heart, including damage taken in The Womb and onwards.', 'Passive Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('143', 'Whore Of Babylon', 'When Isaac reaches a half-heart, he is given +2.16 damage, and his speed increases by 0.3.', 'Passive Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('144', 'Wiggle Worm', 'Gives Isaac waving shots and increases Rate Of Fire by 0.4.', 'Passive Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('145', 'Wire Coat Hanger', 'Increases Tears by 0.7.', 'Passive Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('146', 'Wooden Spoon', 'Increases speed by 0.3.', 'Passive Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('147', 'XRay Vision', 'Reveals and opens Secret Room entrances.', 'Passive Collectible', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('148', '3 Dollar Bill', 'Effect given changes every room.', 'Passive Collectible', 'Wrath of the Lamb', '0', '0');
INSERT INTO `boi` VALUES ('149', 'Abel', 'Mirrors Isaac\'s movements. Shoots in opposite direction to the player.', 'Passive Collectible', 'Wrath of the Lamb', '0', '0');
INSERT INTO `boi` VALUES ('150', 'Ankh', 'When killed, respawns in the previous room as ???, regardless of the fact he is unlocked or not. After respawn, all future HP upgrades will be soul hearts only. Only works once.', 'Passive Collectible', 'Wrath of the Lamb', '0', '0');
INSERT INTO `boi` VALUES ('151', 'The Black Bean', 'When taking damage, Isaac farts, causing poison damage to all enemies in range.', 'Passive Collectible', 'Wrath of the Lamb', '0', '0');
INSERT INTO `boi` VALUES ('152', 'Bloody Lust', 'Increases damage each time Isaac kills an enemy for the current room. (.28 damage per kill, caps at 3.3.)', 'Passive Collectible', 'Wrath of the Lamb', '0', '0');
INSERT INTO `boi` VALUES ('153', 'Bob\'s Curse', 'Gives 5 bombs and grants all bombs poison effect.', 'Passive Collectible', 'Wrath of the Lamb', '0', '0');
INSERT INTO `boi` VALUES ('154', 'Box', 'Spawns 1 of each item: 1 coin, 1 key, 1 bomb, 1 heart, 1 card, 1 pill, 1 trinket.', 'Passive Collectible', 'Wrath of the Lamb', '0', '0');
INSERT INTO `boi` VALUES ('155', 'Bum Friend', 'A portable Beggar who follows Isaac, pick up coins for himself, and occasionally drop Pick Ups. Does not spawn Collectibles. Can push around dropped bombs, similar to Meat Boy.', 'Passive Collectible', 'Wrath of the Lamb', '0', '0');
INSERT INTO `boi` VALUES ('156', 'Cat-O-Nine-Tails', 'Increases Shot Speed by 23%.', 'Passive Collectible', 'Wrath of the Lamb', '0', '0');
INSERT INTO `boi` VALUES ('157', 'Celtic Cross', 'Has a 20% to create a protective shield around Isaac upon taking damage, which grants him invulnerability to all forms of damage for 7 seconds.', 'Passive Collectible', 'Wrath of the Lamb', '0', '0');
INSERT INTO `boi` VALUES ('158', 'Chemical Peel', 'Tears fired from left eye do increased damage.', 'Passive Collectible', 'Wrath of the Lamb', '0', '0');
INSERT INTO `boi` VALUES ('159', 'Daddy\'s Love', 'Daddy Long Legs\' shadow follows Isaac and stomps on enemies.', 'Passive Collectible', 'Wrath of the Lamb', '0', '0');
INSERT INTO `boi` VALUES ('160', 'Dead Dove', 'Gives Isaac spectral tears and flight.', 'Passive Collectible', 'Wrath of the Lamb', '0', '0');
INSERT INTO `boi` VALUES ('161', 'Epic Fetus', 'Tears become guided missiles (See Doctor\'s Remote).', 'Passive Collectible', 'Wrath of the Lamb', '0', '0');
INSERT INTO `boi` VALUES ('162', 'Fate', 'Isaac gains flight and an Eternal Heart.', 'Passive Collectible', 'Wrath of the Lamb', '0', '0');
INSERT INTO `boi` VALUES ('163', 'Ghost Baby', 'Familiar that shoots spectral tears.', 'Passive Collectible', 'Wrath of the Lamb', '0', '0');
INSERT INTO `boi` VALUES ('164', 'Guppy\'s Hair Ball', 'Loosely follows behind Isaac. Can be swung like a flail by rapidly spinning Isaac in a circle. Grows bigger by killing enemies.', 'Passive Collectible', 'Wrath of the Lamb', '0', '0');
INSERT INTO `boi` VALUES ('165', 'Guppy\'s Tail', 'Increases chance of gold chests and normal chests.', 'Passive Collectible', 'Wrath of the Lamb', '0', '0');
INSERT INTO `boi` VALUES ('166', 'Habit', 'When Isaac is hit, 3 points of an item\'s charge are filled.', 'Passive Collectible', 'Wrath of the Lamb', '0', '0');
INSERT INTO `boi` VALUES ('167', 'Harlequin Baby', 'Familiar that shoots a pair of tears diagonally every second while Isaac is firing.', 'Passive Collectible', 'Wrath of the Lamb', '0', '0');
INSERT INTO `boi` VALUES ('168', 'Holy Grail', 'Adds 1 empty heart container and gives flying ability.', 'Passive Collectible', 'Wrath of the Lamb', '0', '0');
INSERT INTO `boi` VALUES ('169', 'Holy Water', 'When Isaac receives damage, the bottle breaks and a puddle of blue Creep forms, which damages non-flying enemies, similar to Lemon Mishap. The bottle is reformed upon entering a different room.', 'Passive Collectible', 'Wrath of the Lamb', '0', '0');
INSERT INTO `boi` VALUES ('170', 'Infestation', '1-3 Blue Flies randomly spawn every time Isaac gets hit.', 'Passive Collectible', 'Wrath of the Lamb', '0', '0');
INSERT INTO `boi` VALUES ('171', 'Ipecac', 'Green projectiles fired from the mouth causing poison/explosive damage.', 'Passive Collectible', 'Wrath of the Lamb', '0', '0');
INSERT INTO `boi` VALUES ('172', 'Jesus Juice', 'Increases Damage by 0.5 and increases Range.', 'Passive Collectible', 'Wrath of the Lamb', '0', '0');
INSERT INTO `boi` VALUES ('173', 'Magic 8 Ball', 'Increases Shot Speed. Drops a Tarot Card when picked up.', 'Passive Collectible', 'Wrath of the Lamb', '0', '0');
INSERT INTO `boi` VALUES ('174', 'MEAT!', 'Adds a heart container, and increases damage.', 'Passive Collectible', 'Wrath of the Lamb', '0', '0');
INSERT INTO `boi` VALUES ('175', 'Mitre', 'Greatly increases the chance of replacing spawned hearts with soul hearts.', 'Passive Collectible', 'Wrath of the Lamb', '0', '0');
INSERT INTO `boi` VALUES ('176', 'Mom\'s Coin Purse', 'Spawns 4 random Pills around Isaac.', 'Passive Collectible', 'Wrath of the Lamb', '0', '0');
INSERT INTO `boi` VALUES ('177', 'Mom\'s Purse', 'Allows Isaac to hold 1 additional trinket.', 'Passive Collectible', 'Wrath of the Lamb', '0', '0');
INSERT INTO `boi` VALUES ('178', 'The Mulligan', 'Chance to spawn Blue Flies on successful hits, which charge current target.', 'Passive Collectible', 'Wrath of the Lamb', '0', '0');
INSERT INTO `boi` VALUES ('179', 'Mutant Spider', 'Shoot four tears at once, greatly reduces tear firing rate.', 'Passive Collectible', 'Wrath of the Lamb', '0', '0');
INSERT INTO `boi` VALUES ('180', 'Pageant Boy', 'Spawns several coins around Isaac.', 'Passive Collectible', 'Wrath of the Lamb', '0', '0');
INSERT INTO `boi` VALUES ('181', 'The Peeper', 'Gives Isaac a familiar eye that floats around the room, damaging enemies on contact. Tears fired from left eye become red (cosmetic only).', 'Passive Collectible', 'Wrath of the Lamb', '0', '0');
INSERT INTO `boi` VALUES ('182', 'Polyphemus', 'Makes Isaac shoot one huge tear. Also reduces Tears to minimum. Damage increased by 4 and then multiplied by 2. If tear does more damage than an enemy\'s life, the tear continues to travel with the extra remaining damage.', 'Passive Collectible', 'Wrath of the Lamb', '0', '0');
INSERT INTO `boi` VALUES ('183', 'Pyro', 'Adds 99 Bombs.', 'Passive Collectible', 'Wrath of the Lamb', '0', '0');
INSERT INTO `boi` VALUES ('184', 'Rainbow Baby', 'Tear Effect changes every shot.', 'Passive Collectible', 'Wrath of the Lamb', '0', '0');
INSERT INTO `boi` VALUES ('185', 'Sacred Heart', 'Shots are larger, travel slower and home in on enemies. Damage is multiplied (Multiplies damage by X2.3+1, -0.4 tears, -0.25 shot speed.). Gives 1 Soul Heart and restores full Health on Pick up.', 'Passive Collectible', 'Wrath of the Lamb', '0', '0');
INSERT INTO `boi` VALUES ('186', 'Sacrificial Dagger', 'Dagger orbits Isaac, very high damage.', 'Passive Collectible', 'Wrath of the Lamb', '0', '0');
INSERT INTO `boi` VALUES ('187', 'Scapular', 'Once per room, when Isaac is reduced to his last half red Heart, the aura around his head temporarily vanishes and he is granted one Soul Heart.', 'Passive Collectible', 'Wrath of the Lamb', '0', '0');
INSERT INTO `boi` VALUES ('188', 'SMB Super Fan!', 'Increases all stats except Speed by 1. Reduces Speed by 1.', 'Passive Collectible', 'Wrath of the Lamb', '0', '0');
INSERT INTO `boi` VALUES ('189', 'Speed Ball', 'Increases movement speed and causes tears to travel faster.', 'Passive Collectible', 'Wrath of the Lamb', '0', '0');
INSERT INTO `boi` VALUES ('190', 'Spirit Of The Night', 'Grants Isaac flight and spectral tears.', 'Passive Collectible', 'Wrath of the Lamb', '0', '0');
INSERT INTO `boi` VALUES ('191', 'Squeezy', '+1 tears and spawns 2 soul hearts.', 'Passive Collectible', 'Wrath of the Lamb', '0', '0');
INSERT INTO `boi` VALUES ('192', 'Stem Cells', 'Adds an extra Heart Container, heals a half heart, and increases Shot Speed.', 'Passive Collectible', 'Wrath of the Lamb', '0', '0');
INSERT INTO `boi` VALUES ('193', 'Stigmata', 'Boosts Isaac\'s damage by 0.3 and grants you one heart container.', 'Passive Collectible', 'Wrath of the Lamb', '0', '0');
INSERT INTO `boi` VALUES ('194', 'Technology 2', 'Fires a continuous laser as you shoot tears. Decreases your damage by -35%. Pierces enemies.', 'Passive Collectible', 'Wrath of the Lamb', '0', '0');
INSERT INTO `boi` VALUES ('195', 'Toothpicks', 'Increases shot speed.', 'Passive Collectible', 'Wrath of the Lamb', '0', '0');
INSERT INTO `boi` VALUES ('196', 'Tough Love', 'Isaac randomly shoots teeth instead of tears, dealing 3.2 times his normal damage.', 'Passive Collectible', 'Wrath of the Lamb', '0', '0');
INSERT INTO `boi` VALUES ('197', 'Half Heart', 'Refills heart containers by half a heart. Randomly replaces Full Hearts when dropped.', 'Pickup', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('198', 'Full Heart', 'Refills heart containers by a heart. If picked up with only missing half a heart, the whole heart will still be consumed.', 'Pickup', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('199', 'Soul Heart', 'Gives 1 additional, non-refillable heart. Randomly replaces Full Hearts when dropped.', 'Pickup', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('200', 'Key', 'Used to unlock locked chests, doors, or blocks.', 'Pickup', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('201', 'Penny', 'Currency of the game. Buy items in the shop with it.', 'Pickup', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('202', 'Nickel', 'Worth 5 pennies. 5% chance of appearing instead of a penny.', 'Pickup', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('203', 'Bomb', 'Blows up an obstacle (rock, doors etc.) or damages enemies. Can be pushed by various things.', 'Pickup', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('204', '1+1 Free', 'Gives 2 bombs. Randomly replaces a normal bomb when spawned.', 'Pickup', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('205', 'Troll Bomb', 'Acts like a lit bomb with an increased Blast Radius. Cannot be picked up. 10% chance to replace a normal bomb drop.', 'Pickup', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('206', 'Tarot Card', 'A Pick Up that can have one of 21 random effects. This effect is revealed upon picking it up.', 'Pickup', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('207', 'Pills', 'A Pick Up that can have various effects. The effect is revealed upon activation, and has a chance of being negative or positive. If the PhD has been obtained, unknown pill types have a 1/150 chance every frame to be revealed. ', 'Pickup', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('208', 'Chest', 'Chests drop a varying amount of Pick Ups when opened. Golden Chests require a key to open, and drop more Pick Ups than regular Chests. They also have a chance of giving Isaac an Item.', 'Pickup', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('209', 'Eternal Heart', 'Grants 1 Eternal Heart, similar to a Soul Heart. Collecting another or completing the floor will turn this into a Full Heart Container. Lost before normal Hearts but after Soul Hearts. Has a 2% chance to replace regular Hearts.', 'Pickup', 'Wrath of the Lamb', '0', '0');
INSERT INTO `boi` VALUES ('210', 'Golden Key', 'Unlocks any door, chest, or block on the current floor without using normal keys. Randomly replaces a normal key when dropped.', 'Pickup', 'Wrath of the Lamb', '0', '0');
INSERT INTO `boi` VALUES ('211', 'Dime', 'Worth 10 pennies. 1% chance of appearing instead of a penny.', 'Pickup', 'Wrath of the Lamb', '0', '0');
INSERT INTO `boi` VALUES ('212', 'Super Troll Bomb', 'Acts like a troll bomb, but follows Isaac around the room until it explodes. 5% chance to replace a normal bomb when spawned.', 'Pickup', 'Wrath of the Lamb', '0', '0');
INSERT INTO `boi` VALUES ('213', 'Red Chest', 'Drops two Soul Hearts, Pills, Spiders, or Troll Bombs. Alternatively, it can drop a Super Troll Bomb, teleport Isaac to the Angel Room/Devil Room, or give him 3 Blue Flies. It can also give any Guppy item except Guppy\'s Hair Ball.', 'Pickup', 'Wrath of the Lamb', '0', '0');
INSERT INTO `boi` VALUES ('214', 'Suit Card', 'Has a chance to replace regular Tarot Cards. Has 5 random effects, which are revealed upon pickup.', 'Pickup', 'Wrath of the Lamb', '0', '0');
INSERT INTO `boi` VALUES ('215', 'Trinkets', 'Passive items that have a hidden effect upon picking them up. Only one trinket can be held at a time, unless the player has Mom\'s Purse.', 'Pickup', 'Wrath of the Lamb', '0', '0');
INSERT INTO `boi` VALUES ('216', 'Bad Gas', 'Poisons any enemies near Isaac. Identical to the item The Bean when activated and the passive effect of The Black Bean.', 'Pill', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('217', 'Bad Trip', 'Causes Isaac to suffer 1 heart of damage, including soul hearts. Turns into a Full Health pill when health is down to 1 red heart (Warning: Bad Trip will only turn into Full Health when total health is down to one heart. In other words, Soul Hearts count). When playing as ???, Bad Trip will convert to a Full Health pill if he is at half a soul heart, rather than a full Soul Heart. Therefore, using it at a full Soul Heart will kill him. This can also happen with negative health (due to Devil Room deals) and 1 Soul Heart as any character.', 'Pill', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('218', 'Balls of Steel', 'Gives the player 2 Soul Hearts. The Soul Hearts are directly added to Isaac\'s health instead of spawning on the floor.', 'Pill', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('219', 'Bombs Are Key', 'Swaps the amount of bombs and keys in the player\'s inventory.', 'Pill', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('220', 'Explosive Diarrhea! (Oh No!)', 'Causes Isaac to leave a short trail of lit Bombs, which do not deplete his current bomb count. (Cannot be obtained with Wrath of the Lamb v1.48. It can only be found in the base game, or v1.50 with the Unholy Edition)', 'Pill', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('221', 'Full Health', 'Fully restores all heart containers. When playing as ??? and at less than 3 soul hearts, will restore health up to 3 soul hearts.', 'Pill', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('222', 'Health Down', 'Decreases Heart Containers by 1. Changes to Health Up when at 1 heart container, regardless of current Soul Heart count.', 'Pill', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('223', 'Health Up', 'Adds an empty Heart Container, and tints Isaac red. This pill doesn\'t count as damage for the purpose of the No Damage Achievements.', 'Pill', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('224', 'I Found Pills (And ate Them!)', 'Isaac will make a \"Durr...\" sound and his appearance will change. (In Wrath of The Lamb, this pill is given one of two random effects: This one and \"Puberty\").', 'Pill', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('225', 'Pretty Fly', 'Adds a Orbital black fly, which blocks enemy shots and damages other Flies.', 'Pill', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('226', 'Range Down', 'Decreases Range by 3.1.', 'Pill', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('227', 'Range Up', 'Increases Range by 3.875. Tints Isaac blue.', 'Pill', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('228', 'Speed Down', 'Decreases Speed by 0.24.', 'Pill', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('229', 'Speed Up', 'Increases Speed by 0.3. Tints Isaac yellow.', 'Pill', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('230', 'Tears Down', 'Decreases Tears by 0.28.', 'Pill', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('231', 'Tears Up', 'Increases Tears by 0.35. Tints Isaac purple.', 'Pill', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('232', 'Telepills', 'Single-use version of the Teleport! item. May teleport to the I AM ERROR room, with a 1/15 (6.66%) chance.', 'Pill', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('233', 'Luck Up', 'Increases Luck. Tints Isaac green.', 'Pill', 'Wrath of the Lamb', '0', '0');
INSERT INTO `boi` VALUES ('234', 'Luck Down', 'Decreases Luck.', 'Pill', 'Wrath of the Lamb', '0', '0');
INSERT INTO `boi` VALUES ('235', 'Friends Till The End!', 'Gives three Attack Flies, similar to the one spawned by The Mulligan.', 'Pill', 'Wrath of the Lamb', '0', '0');
INSERT INTO `boi` VALUES ('236', 'Puberty', 'Hair appears on Isaac\'s head.', 'Pill', 'Wrath of the Lamb', '0', '0');
INSERT INTO `boi` VALUES ('237', '0 The Fool', 'Teleports the player to the starting room of the current level, even if you use the card in the starting room. This card can teleport you out of inescapable rooms like the Challenge Room and the Mom boss fight. It can also teleport you out of problematic rooms in a pinch.', 'Tarot Card', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('238', 'I The Magician', 'Player\'s shots curve towards enemies and may turn purple until you leave the current room. This effect is the same as Spoon Bender and Telepathy For Dummies; this card is useless in the case of the former and best used when the latter lacks charge.', 'Tarot Card', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('239', 'II The High Priestess', 'Summons Mom\'s foot stomp on a random enemy (targets the player if fighting Mom or if there are no enemies present), dealing massive damage, enough to kill most bosses and wiping half of Mom\'s own health if used on her. Acts as an explosion, will destroy rocks and can open secret rooms.', 'Tarot Card', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('240', 'III The Empress', 'Turns the player into a demon, increasing damage by 2 and speed by 1 until you leave the current room. This effect is the same as Whore Of Babylon; as such, this card is useless if you already have this item and it is active.This card is best saved for a room you\'re going to stay in for awhile, such as a Challenge Room or the Boss Room.', 'Tarot Card', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('241', 'IV The Emperor', 'Teleports the player to the Boss Room. If used while fighting the boss, the fight will restart and the boss will be back at full health. (Will be the same boss, but could possibly change to a different colored version)', 'Tarot Card', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('242', 'V The Hierophant', 'Spawns 2 soul hearts in the room.', 'Tarot Card', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('243', 'VI The Lovers', 'Spawns 2 full hearts in the room.', 'Tarot Card', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('244', 'VII The Chariot', 'Grants you temporary invincibility, and causes you to damage enemies with touch. However, you cannot attack unless you\'re holding Mom\'s Knife. This is the same effect as My Little Unicorn.', 'Tarot Card', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('245', 'VIII Justice', 'Spawns 1 random type of Bomb drop, Key drop, Coin drop, and Heart drop. This can spawn anything dropped by clearing a room, like Troll Bombs, 1+1 Free, Soul Hearts, and nickels. With Wrath of the Lamb, you can also get Super Troll Bombs, Eternal Hearts, Golden Keys and dimes from using this card. This card will spawn a specific kind of heart if a room forces it (full red heart in the womb-themed top secret room, eternal heart in the cathedral-themed top secret room).', 'Tarot Card', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('246', 'IX The Hermit', 'Teleports the player to the Shop. If there is no Shop (or you are already in one), this will act as a random teleport. This card can teleport you into a locked Shop, and you will open it without consuming a key if it does.', 'Tarot Card', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('247', 'X Wheel of Fortune', 'Spawns a Slot Machine. In Wrath of the Lamb, this may also spawn a Fortune Telling Machine. If you can, find a room with another Slot Machine, Fortune Telling Machine, or Blood Donation Machine to maximize gambling efficiency.', 'Tarot Card', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('248', 'XI Strength', 'Makes the player bigger, increasing maximum health and damage until the player leaves the current room (same effect as Odd Mushroom (Large)). ??? receives a permanent spirit heart rather than a temporary maximum health increase, however this may be a bug. This card is best saved for a room you\'re going to stay in for awhile, such as a challenge room or the boss room; it can also be used as a last-ditch healing effort.', 'Tarot Card', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('249', 'XII The Hanged Man', 'Removes the player\'s body allowing flight over obstacles, spikes and pits until you leave the current room. If you have Brimstone, instead of removing the players body it will give them wings (All the same as single-use Transcendence).', 'Tarot Card', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('250', 'XIII Death', 'Damages all enemies in the room significantly. This does the same thing as the The Necronomicon, and they can be devastating to enemies in the room if you use them in quick succession. This technique is best saved for Bosses, though; especially those that like to spawn lesser enemies.', 'Tarot Card', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('251', 'XIV Temperance', 'Spawns a Blood Donation Machine. If you can, find a room with another Slot Machine, Fortune Telling Machine, or Blood Donation Machine to maximize gambling efficiency.', 'Tarot Card', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('252', 'XV The Devil', 'Increases your damage by 2 until you leave the room. This has the exact same effect as The Book Of Belial. Using both in the same room will not cause the damage to stack.', 'Tarot Card', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('253', 'XVI The Tower', 'Spawns six troll bombs around the room. This does the same thing as the Anarchist Cookbook, and they can be devastating to both yourself and enemies in the room if you use them in quick succession.', 'Tarot Card', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('254', 'XVII The Stars', 'Teleports the player to the Treasure Room. If there is no Treasure Room, this will act as a random teleport. Randomly chooses a Treasure Room in XL floors. This card can teleport you into a locked Treasure Room, and you will open it without consuming a key if it does.', 'Tarot Card', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('255', 'XVIII The Moon', 'Teleports the player to the Secret Room. This card will open one random wall once inside the Secret Room, so you can leave even if you don\'t have any bombs available. This card can teleport you out of inescapable rooms like the Challenge Room and Mom\'s room. It can also get you out of problematic rooms in a pinch.', 'Tarot Card', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('256', 'XIX The Sun', 'Completely heals player, damages all enemies, and reveals the entire map and location of special rooms for the current level. (Same effect as XXI The World and a Full Health pill combined, though unlike a Full Health Pill, it will not heal ???.)', 'Tarot Card', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('257', 'XX The Judgement', 'Spawns a beggar. In Wrath of the Lamb, this may also spawn a Devil Beggar. Try to wait until you get into a Devil Room or an Angel Room to use this card; if you do and give the Beggar enough money, he will leave behind an item specific to that room instead of a more random item. If you cannot get either before leaving this card behind, use it in the Curse Room or the Secret Room.', 'Tarot Card', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('258', 'XXI The World', 'Reveals the entire map and location of all special rooms for the current level. This is less useful if you have either Treasure Map or The Compass, and completely useless if you have both, or you\'ve revealed the map via things like the Crystal Ball, a Sun card or another World card.', 'Tarot Card', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('259', 'The Joker', 'Transports you to the Devil Room. Exiting places you in the starting room of the floor. The Devil Room acts as usual: it may randomly contain Krampus, get replaced by an Angel Room and will contain a trapdoor to Sheol in Level 2 of Chapter 4. Therefore if you access it on a floor before fighting the boss, take the items inside and get regular access to it later in the boss room, the Devil Room won\'t contain new items. Also allows getting to the Devil Room in situations in which it would be otherwise inaccessible.', 'Tarot Card', 'Wrath of the Lamb', '0', '0');
INSERT INTO `boi` VALUES ('260', '2 of Hearts', 'Doubles your Hearts (up to full health). If you have half a heart left, recovers your first two Heart Containers. (Spirit Hearts are unaffected.) Useless for ???.', 'Tarot Card', 'Wrath of the Lamb', '0', '0');
INSERT INTO `boi` VALUES ('261', '2 of Spades', 'Doubles the number of Keys you currently have. If you don\'t have any Keys, provides you with two.', 'Tarot Card', 'Wrath of the Lamb', '0', '0');
INSERT INTO `boi` VALUES ('262', '2 of Clubs', 'Doubles the number of Bombs you currently have. If you don\'t have any Bombs, provides you with two.', 'Tarot Card', 'Wrath of the Lamb', '0', '0');
INSERT INTO `boi` VALUES ('263', '2 of Diamonds', 'Doubles the number of Coins you currently have. If you don\'t have any Pennies, provides you with two.', 'Tarot Card', 'Wrath of the Lamb', '0', '0');
INSERT INTO `boi` VALUES ('264', '???\'s Soul', 'Gives you a Familiar that looks like Brother Bobby and floats slowly around the room on a pattern similar to The Peeper, shooting long-range spectral homing tears that have high Shot Speed. If you have other familiars, they will follow ???\'s Soul instead of Isaac. This does not include Orbitals, Blue Flies, or stage 3/4 Meat Boy and the body created by The Pinking Shears. This item is meant to be unlocked by completing The Chest with ???. However, due to a bug it is instead unlocked by completing The Chest with Eve.', 'Trinket', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('265', 'Ace Of Spades', 'Chance of finding a Card when clearing a room is increased by 10%. Chance of finding a Card when opening a Chest is increased by 66%.', 'Trinket', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('266', 'Bloody Penny', 'Gives a 50% chance to drop a Half Heart when money is collected. Unlocked by completing Cathedral with Samson. Hearts dropped through the use of this trinket will always be Half Hearts, and are not affected by items such as Mitre or being spawned in special rooms such as Cathedral Super Secret Rooms. Effective trinket for Blood Donation Machine use, as the money dropped by the machine will often create a Half Heart, effectively making that use \"free\". This is even more pronounced with the IV Bag, since it is possible to create as many as four coin drops per Half Heart spent via the item.', 'Trinket', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('267', 'Burnt Penny', 'Gives a 50% chance to drop a bomb when money is collected. Unlocked by completing the Large Marge challenge. Bombs dropped through the use of this trinket will always be single Bomb pickups.', 'Trinket', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('268', 'Cain\'s Eye', 'When starting a floor, 25% chance to get the The Compass effect for the duration of a floor. Unlocked by completing The Chest with Cain.', 'Trinket', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('269', 'Cancer', 'Decreases Tears Delay by 2. The effective fire rate increase varies depending on Isaac\'s current Tears stat. The higher the Tears stat, the higher the percentage increase in fire rate from Cancer.', 'Trinket', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('270', 'Child\'s Heart', 'Chance of finding a Heart when clearing a room is increased by 10%. Chance of finding a Heart when opening a Chest is increased by 66%. Hearts dropped due to this trinket are treated as normal heart drops: they have a small chance to appear as Soul Hearts or Eternal Hearts. Items such as Mitre or Mom\'s Pearl will affect this chance.', 'Trinket', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('271', 'Counterfeit Penny', 'Gives a 50% chance of increasing the value of every coin pickup by one. Can only be found in Golden Poop. Unlocked by completing the Isaac Was Good Today challenge.', 'Trinket', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('272', 'Cursed Skull', 'If Isaac has less than one full Red Heart after taking damage, he will be immediately teleported to the last cleared room he was in. The Cursed Skull only counts Red Hearts, and does not count Soul Hearts or Eternal Hearts. Even if the triggering damage is absorbed by a Soul Heart or Eternal Heart, Isaac will still be teleported if he has half a Red Heart or less. Thus, ??? will be teleported any time he takes damage while holding this trinket, as will any character who has reduced their heart count to zero via other means.', 'Trinket', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('273', 'Curved Horn', 'Increases Effective Damage by 2. Unlocked by completing The Cathedral with Judas. Rather than increasing the Damage stat, the Curved Horn increases the Effective Damage of Isaac\'s tears. Because the Damage stat increases Effective Damage by smaller and smaller amounts with each increase, this makes the trinket considerably more valuable at higher levels of the Damage stat.', 'Trinket', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('274', 'Eve\'s Bird Foot', '5% chance to spawn a Dead Bird familiar for the current room each time an enemy dies. This item is meant to be unlocked by completing The Chest with Eve. However, due to a bug it is instead unlocked by completing The Chest with ???.', 'Trinket', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('275', 'Flat Penny', 'Gives a 50% chance of spawning a Key upon collecting a penny. Keys dropped through use of this trinket are treated as normal Key drops, and have a small chance of being replaced by a Golden Key.', 'Trinket', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('276', 'Fish Head', 'Attack flies spawn every time Isaac gets hit. Unlocked by completing the Lord of the Flies challenge.', 'Trinket', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('277', 'Goat Hoof', 'Increases speed by 0.15. Can exceed normal upper bound on speed.', 'Trinket', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('278', 'Isaac\'s Head', 'Gives the player Isaac\'s severed head as a tear-firing familiar. Fires piercing tears. Unlocked by completing The Chest with Isaac. Tears fired by Isaac\'s head do the same amount of damage as those fired by Brother Bobby.', 'Trinket', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('279', 'Isaac\'s Fork', 'Clearing rooms may heal Isaac by 1/2 of a heart (10% chance).', 'Trinket', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('280', 'Judas\' Tongue', 'Items in the Devil Room only cost 1 heart. Soul heart trades still cost 3 Soul hearts. Only works if you have the trinket before revealing the Devil Room. Unlocked by completing The Chest with Judas.', 'Trinket', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('281', 'The Left Hand', 'Replaces all normal chests with Red Chests. Gold chests are affected as well. Unlocked by defeating Ultra Pride.', 'Trinket', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('282', 'Liberty Cap', '25% chance of giving Isaac a random passive effect from Mushrooms as well as its corresponding visual effect upon entering a room. Possible mushrooms: Mini Mushroom, Odd Mushroom (Large), Odd Mushroom (Thin).', 'Trinket', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('283', 'Lucky Toe', 'Increases the chance of a generic drop from clearing a room by roughly 10%, the same amount increased by the Lucky Foot item. If Isaac\'s Luck stat is not negative, this raises the chance of an item dropping from 86.6% to 96.3%. If Isaac also has the Lucky Foot and his Luck stat is not negative, the chance for a drop is only increased by 2% instead. This raises the chance of an item dropping to 98.3%. If Isaac has a negative Luck stat and the Lucky Toe with or without the Lucky Foot, there is a 100% chance that an item will drop after clearing a room. The increase in drop chance is applied before the effect of Guppy\'s Tail. Unlocked by Completing the 7 Years Bad Luck challenge.', 'Trinket', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('284', 'Maggy\'s Faith', 'Gives an eternal heart at the start of every floor. Unlocked by completing The Chest with Magdalene.', 'Trinket', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('285', 'Match Stick', 'Chance of finding a Bomb when clearing a room is increased by 10%. Chance of finding a Bomb when opening a Chest is increased by 66%. Bombs dropped due to this trinket are treated as normal Bomb drops, and can appear as single pickups, double pickups, or even Troll Bombs.', 'Trinket', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('286', 'A Missing Page', '5% chance to induce The Necronomicon\'s effect upon taking damage.', 'Trinket', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('287', 'Mom\'s Pearl', 'Grants a secondary 10% chance of any non-specific Heart drop turning into a Soul Heart. The secondary chance triggers whenever a non-specific Heart drops which does not become an Eternal Heart or Soul Heart. It is applied after Eve\'s secondary 6.25% chance, but before Mitre\'s secondary 50% chance. By itself, Mom\'s Pearl raises the chance of a non-specific Heart dropping as a Soul Heart from 9.8% to 18.62%. Combined with Eve\'s bonus, the chance becomes 23.58%. Combined with Mitre, the chance is 58.31%. Combined with both Mitre and Eve\'s bonus for the highest possible bonus, the chance is raised to 60.79%.', 'Trinket', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('288', 'Pinky Eye', '10% chance to fire a poison tear. There\'s a possibility to fire poisonous bombs with Dr. Fetus.', 'Trinket', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('289', 'The Polaroid', 'If Isaac survives damage with less than one red heart, he will receive a shield similar to the Book Of Shadows for 5 seconds. This effect ignores other heart types and thus always works for ???. Holding The Polaroid while opening the big chest at the end of the Cathedral will allow you to continue to The Chest. Unlocked by completing Cathedral six times. You may use the same characters for all six completions. In 1.4 through 1.48 it is always dropped by Mom upon completion of The Depths 2.', 'Trinket', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('290', 'Push Pin', '10% chance to fire a spectral and piercing tear.', 'Trinket', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('291', 'Red Patch', '20% chance to increase damage upon taking hit, as if Razor Blade was used. Isaac loses his skin for the duration of the room. If Isaac has flight, loses body as well (visual effect similar to Transcendence).', 'Trinket', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('292', 'Rusted Key', 'Chance of finding a Key or a Golden Chest when clearing a room is increased by 5% for each. Chance of finding a Key when opening a Chest is increased by 66%. This item has no effect in v1.48 and above.', 'Trinket', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('293', 'Safety Cap', 'Chance of finding a Pill when clearing a room is increased by 10%. Chance of finding a Pill when opening a Chest is increased by 66%.', 'Trinket', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('294', 'Samson\'s Lock', '1/15 chance to increase damage by 0.5 for the current room each time an enemy dies. Stacks with Bloody Lust, but chance to trigger is too low to be worthwhile. Unlocked by completing The Chest with Samson.', 'Trinket', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('295', 'The Tick', 'Drain 15% health of each enemy with more than 60 HP (ie. most bosses). Also restores one red heart of Isaac whenever he enters an uncleared boss room. The Tick is stuck into Isaac\'s head and cannot be swapped out for another trinket. Bosses like Larry Jr. / Fistula are made up of entities with less than 60 HP, so their HP are not leeched. Might be disadvantageous when playing with Eve, as Whore of Babylon will deactivate when entering boss room due to restored health. The player will be unable to access The Chest without the use of Mom\'s Purse, as the nature of this trinket makes it impossible to pick up The Polaroid.', 'Trinket', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('296', 'Umbilical Cord', 'When Isaac\'s health is brought to half a red heart, a Little Steve familiar spawns. Applies only to the room in which Isaac took damage. If Isaac moves to another room without regaining more than a half heart of health, the spawned familiar will still disappear. If you already have a Little Steve, Brother Bobby will spawn instead.', 'Trinket', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('297', 'Isaac', '3 Health, 2 Speed, 2 Tears, 2 Attack, 2 Range. Starts with 1 Bomb and The Dice if unlocked (By beating The Womb with ???)', 'Character', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('298', 'Magdalene', '4 Health, 1 Speed, 2 Tears, 2 Attack, 2 Range. Starts with Yum Heart. Unlocked by having seven or more Heart Containers in one playthrough (Soul Hearts do not count).', 'Character', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('299', 'Cain', '2 Health, 3 Speed, 2 Tears, 3 Attack, 1 Range. Starts with 1 key and Lucky Foot. Unlocked by having 55 or more coins at any time during a playthrough.', 'Character', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('300', 'Judas', '1 Health, 2 Speed, 2 Tears, 4 Attack, 2 Range. Starts with 3 pennies and The Book Of Belial. Unlocked after defeating Mom\'s Heart.', 'Character', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('301', 'Eve', '2 Health, 3 Speed, 2 Tears, 1 Attack, 2 Range. Starts with Dead Bird and Whore Of Babylon. Unlocked by making two or more deals with the devil in one playthrough even if it kills you.', 'Character', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('302', '???', 'No Health, 2 Speed, 2 Tears, 2 Attack, 2 Range. Starts with 3 Soul Hearts and The Poop. Can not have hearts. Items that would normally increase max health add Soul Hearts instead. Normal heart pickups and healing has no effect. Unlocked after defeating It Lives.', 'Character', 'Vanilla', '0', '0');
INSERT INTO `boi` VALUES ('303', 'Samson', '1 Health, 2 Speed,, 2 Tears, 2 Attack, 2 Range. Starts with 1 Soul Heart and Bloody Lust. Unlocked by skipping two Treasure Rooms and defeating Mom.', 'Character', 'Wrath of the Lamb', '0', '0');
